const sleep = (milliseconds) => {
    return new Promise(resolve => setTimeout(resolve, milliseconds))
}

var colors = new Array(
    [62,35,255],
    [60,255,60],
    [255,35,98],
    [45,175,230],
    [255,0,255],
    [255,128,0]);
  
  var step = 0;
  //color table indices for: 
  // current color left
  // next color left
  // current color right
  // next color right
  var colorIndices = [0,1,2,3];
  
  //transition speed
  var gradientSpeed = 0.002;
  
  function updateGradient()
  {
    
    if ( $===undefined ) return;
    
  var c0_0 = colors[colorIndices[0]];
  var c0_1 = colors[colorIndices[1]];
  var c1_0 = colors[colorIndices[2]];
  var c1_1 = colors[colorIndices[3]];
  
  var istep = 1 - step;
  var r1 = Math.round(istep * c0_0[0] + step * c0_1[0]);
  var g1 = Math.round(istep * c0_0[1] + step * c0_1[1]);
  var b1 = Math.round(istep * c0_0[2] + step * c0_1[2]);
  var color1 = "rgb("+r1+","+g1+","+b1+")";
  
  var r2 = Math.round(istep * c1_0[0] + step * c1_1[0]);
  var g2 = Math.round(istep * c1_0[1] + step * c1_1[1]);
  var b2 = Math.round(istep * c1_0[2] + step * c1_1[2]);
  var color2 = "rgb("+r2+","+g2+","+b2+")";
  
   $('#gradient').css({
     background: "-webkit-gradient(linear, left top, right top, from("+color1+"), to("+color2+"))"}).css({
      background: "-moz-linear-gradient(left, "+color1+" 0%, "+color2+" 100%)"});
    
    step += gradientSpeed;
    if ( step >= 1 )
    {
      step %= 1;
      colorIndices[0] = colorIndices[1];
      colorIndices[2] = colorIndices[3];
      
      //pick two new target color indices
      //do not pick the same as the current one
      colorIndices[1] = ( colorIndices[1] + Math.floor( 1 + Math.random() * (colors.length - 1))) % colors.length;
      colorIndices[3] = ( colorIndices[3] + Math.floor( 1 + Math.random() * (colors.length - 1))) % colors.length;
      
    }
  }
  
  setInterval(updateGradient,10);

function endsWith(str, suffix) {
    return str.indexOf(suffix, str.length - suffix.length) !== -1;
}


async function getToken(){
    _from = web3.currentProvider.selectedAddress
    _contractAddress = document.getElementById("contractAdd").value
    _contractAbi = abi
    _token = document.getElementById("token").value

    const contract = new web3.eth.Contract(_contractAbi, _contractAddress)
    const ipfsURI = await contract.methods.tokenURI(_token).call({from: _from})
    ipfsUriSplit = ipfsURI.split("/")
    gateway = "https://gateway.pinata.cloud/ipfs/"
    fullPath = gateway + ipfsUriSplit[ipfsUriSplit.length - 2] + "/" + ipfsUriSplit[ipfsUriSplit.length - 1]
    await fetch(fullPath)
    .then((response) => response.json())
    .then((data) => {
        imgUriSplit = data.image.split("/")
        fullImgPath = gateway + imgUriSplit[imgUriSplit.length - 2] + "/" + imgUriSplit[imgUriSplit.length - 1]
        if(endsWith(fullImgPath, "mp4")){
            document.getElementById("IpfsLink").setAttribute("href", fullImgPath)
            document.getElementById("IpfsLink").innerHTML = fullImgPath
            document.getElementById("NftVideo").setAttribute("style", "display: block; max-width: 350px; max-height: 350px;")
            document.getElementById("NftVidSrc").setAttribute("src", fullImgPath)
        }else{
            document.getElementById("IpfsLink").setAttribute("href", fullImgPath)
            document.getElementById("IpfsLink").innerHTML = fullImgPath
            document.getElementById("NftImage").setAttribute("style", "display: block; max-width: 350px; max-height: 350px;")
            document.getElementById("NftImage").setAttribute("src", fullImgPath)
        }
        
    })
}


async function loadWeb3(enabled) {
	try{
		if (window.ethereum) {
		  window.web3 = new Web3(window.ethereum);
		  try {
		    // Request account access
		    if(!enabled){
		    	await window.ethereum.enable()
                document.getElementById("connectBtn").innerHTML = "connected"
		    }
            else{
                document.getElementById("connectBtn").innerHTML = "connected"
            }
		  } catch (error) {
		    // User denied account access...
		    console.error("User denied account access")
		  }
		  return window.web3
		}
		// Legacy dapp browsers...
		else if (window.web3) {
		  window.web3 = (window.web3.currentProvider);
		  return window.web3
		}
		else {
		  alert("Web3 enabled browser now found. You should consider using MetaMask.")
		  return
		}
	}catch(e){
		if(enabled){
			try{
				loadWeb3(true)
			}catch{
				return
			}
		}
	}
}